// src/App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [progress, setProgress] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const pollProgress = (selectedModel) => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        if (data.progress >= 100) {
          clearInterval(interval);
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(interval);
      }
    }, 1000);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setProgress(0);
    setImageUrl('');
    setLoading(true);

    // Start polling for progress.
    pollProgress(model);

    // Send a POST request to generate the image.
    const response = await fetch('http://localhost:8080/generate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt, model }),
    });

    const data = await response.json();
    setImageUrl(data.imageUrl);
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>Image Generator</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt"
          required
        />
        <select value={model} onChange={(e) => setModel(e.target.value)}>
          <option value="model1">Asian Model</option>
          <option value="model2">Anime Model</option>
          <option value="dalle">DALLÂ·E</option>
        </select>
        <button type="submit" disabled={loading}>
          Generate Image
        </button>
      </form>
      {loading && (
        <div>
          <progress value={progress} max="100"></progress>
          <p>{progress}%</p>
        </div>
      )}
      {imageUrl && (
        <div>
          <h2>Generated Image:</h2>
          {/* Prepend the backend URL since imageUrl is served from /images */}
          <img src={`http://localhost:8080${imageUrl}`} alt="Generated" style={{ maxWidth: '100%' }} />
        </div>
      )}
    </div>
  );
}

export default App;
