import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import ChatBot from './ChatBot';

function App() {
  // Core state variables.
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [galleryImages, setGalleryImages] = useState([]);
  const [selectedGalleryImage, setSelectedGalleryImage] = useState(null);
  const [showGallery, setShowGallery] = useState(false);
  const pollingIntervalRef = useRef(null);

  // Fetch gallery images on mount and after generation.
  useEffect(() => {
    fetchGallery();
  }, [loading]);

  const fetchGallery = async () => {
    try {
      const res = await fetch('http://localhost:8080/gallery');
      const data = await res.json();
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  const pollProgress = (selectedModel) => {
    if (pollingIntervalRef.current) {
      clearInterval(pollingIntervalRef.current);
    }
    pollingIntervalRef.current = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) {
          clearInterval(pollingIntervalRef.current);
          pollingIntervalRef.current = null;
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(pollingIntervalRef.current);
        pollingIntervalRef.current = null;
      }
    }, 1000);
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setImageError(false);
    setLoading(true);

    if (model !== 'dalle') {
      pollProgress(model);
    }
    try {
      const response = await fetch('http://localhost:8080/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, model }),
      });
      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }
      const data = await response.json();
      if (pollingIntervalRef.current) {
        clearInterval(pollingIntervalRef.current);
      }
      if (data.imageUrl) {
        setImageUrl(data.imageUrl);
      } else {
        setImageError(true);
      }
      fetchGallery();
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async () => {
    await fetch('http://localhost:8080/cancel', { method: 'POST' });
    setLoading(false);
    setProgress(0);
    setEta(0);
  };

  const handleRegenerate = () => {
    setPrompt('');
    setImageUrl('');
    setProgress(0);
    setEta(0);
    setImageError(false);
  };

  const getImageUrl = () => {
    let url = imageUrl.startsWith("http")
      ? imageUrl
      : `http://localhost:8080${imageUrl}`;
    return `${url}?t=${new Date().getTime()}`;
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="app-header">
        <h1>AI Image Creator</h1>
        <p className="tagline">Unleash Your Creativity with AI-Powered Image Generation</p>
      </header>

      <div className="main-layout">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="logo">
            {/* Optional logo/icon */}
            <h2>Menu</h2>
          </div>
          <button 
            className={`sidebar-button ${showGallery ? 'active' : ''}`} 
            onClick={() => setShowGallery(!showGallery)}>
            Gallery
          </button>
          {/* Future navigation items or user info can be added here */}
        </aside>

        {/* Main Workspace */}
        <main className="main-workspace">
          {showGallery ? (
            <div className="gallery-view">
              <h2>Gallery</h2>
              {galleryImages.length > 0 ? (
                <div className="gallery-slider">
                  {galleryImages.map((imgUrl, idx) => (
                    <img
                      key={idx}
                      src={imgUrl}
                      alt={`Gallery ${idx}`}
                      className="gallery-item"
                      onClick={() => setSelectedGalleryImage(imgUrl)}
                    />
                  ))}
                </div>
              ) : (
                <p>No images available.</p>
              )}
            </div>
          ) : (
            <div className="generator-view">
              <form onSubmit={handleGenerate} className="generator-form">
                <div className="input-panel">
                  <div className="dropdown-container">
                    <label htmlFor="model-select">Model Type:</label>
                    <select 
                      id="model-select"
                      value={model}
                      onChange={(e) => setModel(e.target.value)}>
                      <option value="model1">Model A</option>
                      <option value="model2">Model B</option>
                      <option value="dalle">DALLÂ·E</option>
                    </select>
                  </div>
                  <textarea
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    placeholder="Enter your prompt here..."
                    required
                    className="prompt-input"
                  />
                  <button type="submit" className="generate-button" disabled={loading}>
                    {loading ? 'Generating...' : 'Generate'}
                    {loading && <span className="spinner"></span>}
                  </button>
                </div>
              </form>

              {loading && (
                <div className="progress-container">
                  {model === "dalle" ? (
                    <div className="spinner-container">
                      <div className="loader"></div>
                      <p>Loading... (estimated 30 seconds)</p>
                    </div>
                  ) : (
                    <div className="progress-bar-container">
                      <progress value={progress} max="100" className="progress-bar"></progress>
                      <div className="progress-info">
                        <span>{progress}%</span>
                        <span>ETA: {eta} sec</span>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {imageUrl && (
                <div className="image-container">
                  <h2>Generated Image:</h2>
                  {imageError ? (
                    <p className="error-text">Image generation failed or could not be loaded.</p>
                  ) : (
                    <img
                      src={getImageUrl()}
                      alt="Generated"
                      className="generated-image"
                      onError={() => setImageError(true)}
                    />
                  )}
                  <button className="regenerate-button" onClick={handleRegenerate}>New Prompt</button>
                </div>
              )}

              {loading && (
                <button type="button" onClick={handleCancel} className="cancel-button">
                  Cancel
                </button>
              )}
            </div>
          )}
        </main>
      </div>

      {/* Gallery Modal */}
      {selectedGalleryImage && (
        <div className="gallery-modal" onClick={() => setSelectedGalleryImage(null)}>
          <img
            src={selectedGalleryImage}
            alt="Expanded Gallery"
            className="expanded-gallery-image"
          />
        </div>
      )}

      {/* ChatBot Component */}
      <ChatBot />
    </div>
  );
}

export default App;
