import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import ChatBot from './ChatBot';

function App() {
  // View state: "create" or "gallery"
  const [view, setView] = useState("create");

  // Core states for generation and gallery
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [galleryImages, setGalleryImages] = useState([]);
  const [selectedGalleryImage, setSelectedGalleryImage] = useState(null);

  const pollingIntervalRef = useRef(null);

  // Fetch gallery images from the backend
  const fetchGallery = async () => {
    try {
      const res = await fetch('http://localhost:8080/gallery');
      const data = await res.json();
      console.log("Fetched gallery images:", data.images);
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  // Fetch gallery on mount and when view changes
  useEffect(() => {
    fetchGallery();
  }, [loading, view]);

  // Poll backend for progress updates
  const pollProgress = (selectedModel) => {
    if (pollingIntervalRef.current) {
      clearInterval(pollingIntervalRef.current);
    }
    pollingIntervalRef.current = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) {
          clearInterval(pollingIntervalRef.current);
          pollingIntervalRef.current = null;
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(pollingIntervalRef.current);
        pollingIntervalRef.current = null;
      }
    }, 1000);
  };

  // Handle image generation
  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setImageError(false);
    setLoading(true);

    // For non-DALL¬∑E models, start progress polling.
    let progressInterval;
    if (model !== 'dalle') {
      progressInterval = pollProgress(model);
    }

    try {
      const response = await fetch('http://localhost:8080/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, model }),
      });
      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }
      const data = await response.json();

      if (progressInterval) {
        clearInterval(progressInterval);
      }

      if (data.imageUrl) {
        setImageUrl(data.imageUrl);
      } else {
        setImageError(true);
      }
      // Refresh the gallery images.
      fetchGallery();
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  // Cancel ongoing generation.
  const handleCancel = async () => {
    await fetch('http://localhost:8080/cancel', { method: 'POST' });
    setLoading(false);
    setProgress(0);
    setEta(0);
  };

  // Reset input for new prompt.
  const handleRegenerate = () => {
    setPrompt('');
    setImageUrl('');
    setProgress(0);
    setEta(0);
    setImageError(false);
  };

  // Ensure the image URL is absolute and cache-busted.
  const getImageUrl = () => {
    let url = imageUrl.startsWith("http") ? imageUrl : `http://localhost:8080${imageUrl}`;
    return `${url}?t=${new Date().getTime()}`;
  };

  // Switch between "create" and "gallery" views.
  const switchToGallery = () => setView("gallery");
  const switchToCreate = () => setView("create");

  return (
    <div className="app-container">
      {/* Header Section */}
      <header className="app-header">
        <div className="header-content">
          <h1>AI Image Creator</h1>
          <p className="tagline">Unleash Your Creativity with AI-Powered Image Generation</p>
        </div>
      </header>

      <div className="app-layout">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="logo">
            <span role="img" aria-label="logo">üé®</span>
          </div>
          <nav className="nav">
            <button
              className={`nav-button ${view === "create" ? "active" : ""}`}
              onClick={switchToCreate}
            >
              <span role="img" aria-label="create">‚úèÔ∏è</span> Create
            </button>
            <button
              className={`nav-button ${view === "gallery" ? "active" : ""}`}
              onClick={switchToGallery}
            >
              <span role="img" aria-label="gallery">üñºÔ∏è</span> Gallery
            </button>
          </nav>
        </aside>

        {/* Main Workspace */}
        <main className="main-workspace">
          {view === "create" && (
            <div className="create-panel">
              <form onSubmit={handleGenerate} className="prompt-form">
                <div className="input-group">
                  <label htmlFor="model-select" className="input-label">Model Type:</label>
                  <select
                    id="model-select"
                    value={model}
                    onChange={(e) => setModel(e.target.value)}
                    className="model-select"
                  >
                    <option value="model1">Model A</option>
                    <option value="model2">Model B</option>
                    <option value="dalle">DALL¬∑E</option>
                  </select>
                </div>
                <div className="input-group">
                  <label htmlFor="prompt-input" className="input-label">Prompt:</label>
                  <textarea
                    id="prompt-input"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    placeholder="Enter your prompt here..."
                    required
                    className="prompt-input"
                  />
                </div>
                <div className="button-group">
                  {imageUrl ? (
                    <button type="button" onClick={handleRegenerate} className="regenerate-button">
                      New Prompt
                    </button>
                  ) : (
                    !loading && (
                      <button type="submit" className="generate-button">
                        <span role="img" aria-label="magic">‚ú®</span> Generate
                      </button>
                    )
                  )}
                  {loading && (
                    <>
                      <button type="button" onClick={handleCancel} className="cancel-button">
                        Cancel
                      </button>
                      <button type="submit" disabled className="generate-button disabled">
                        Generating...
                      </button>
                    </>
                  )}
                </div>
              </form>

              {/* Loading and Progress Feedback */}
              {loading && model === "dalle" && (
                <div className="spinner">
                  <div className="loader"></div>
                  <p>Loading... (estimated 30 seconds)</p>
                </div>
              )}
              {loading && model !== "dalle" && (
                <div className="progress-container">
                  <progress value={progress} max="100" className="progress-bar"></progress>
                  <div className="progress-info">
                    <span className="progress-text">{progress}%</span>
                    <span className="eta-text">ETA: {eta} sec</span>
                  </div>
                </div>
              )}

              {/* Generated Image Display */}
              {imageUrl && (
                <div className="image-container">
                  <h2>Generated Image:</h2>
                  {imageError ? (
                    <p className="error-text">Image generation failed or could not be loaded.</p>
                  ) : (
                    <img
                      src={getImageUrl()}
                      alt="Generated"
                      className="generated-image"
                      onError={() => setImageError(true)}
                    />
                  )}
                </div>
              )}
            </div>
          )}

          {view === "gallery" && (
            <div className="gallery-panel">
              <h2>Gallery</h2>
              <div className="gallery-grid">
                {galleryImages.length > 0 ? (
                  galleryImages.map((imgUrl, idx) => (
                    <img
                      key={idx}
                      src={imgUrl}
                      alt={`Gallery ${idx}`}
                      className="gallery-item"
                      onClick={() => setSelectedGalleryImage(imgUrl)}
                    />
                  ))
                ) : (
                  <p>No images available.</p>
                )}
              </div>
            </div>
          )}
        </main>
      </div>

      {/* Gallery Modal */}
      {selectedGalleryImage && (
        <div className="gallery-modal" onClick={() => setSelectedGalleryImage(null)}>
          <img
            src={selectedGalleryImage}
            alt="Expanded Gallery"
            className="expanded-gallery-image"
          />
        </div>
      )}

      {/* ChatBot Component */}
      <ChatBot />
    </div>
  );
}

export default App;
