import React, { useState, useEffect, useRef } from "react";
import "./App.css";

/********************************************
 * HELPER: For random suggestions (bubbles)  *
 ********************************************/
const allSuggestions = [
  "sunset", "mountains", "forest", "cityscape", "abstract",
  "vintage", "futuristic", "space", "cyberpunk", "waterfall",
  "beach", "night sky", "aurora", "rainforest", "desert",
  "ocean", "galaxy", "minimal", "vibrant", "surreal"
];

/********************************************
 * HELPER: Generate random bubble positions  *
 ********************************************/
function getRandomPos() {
  // random X between 0 and 80% (vw)
  // random Y between 0 and 80% (vh)
  const x = Math.random() * 80;
  const y = Math.random() * 80;
  return { x, y };
}

function App() {
  /********************************************
   *  STATES & REFS
   ********************************************/
  const [prompt, setPrompt] = useState("");
  const [model, setModel] = useState("model1"); // can be "model1", "model2", or "dalle"
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);

  const [imageUrl, setImageUrl] = useState("");
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);

  // UI states
  const [darkMode, setDarkMode] = useState(true);

  // We'll fade out the welcome + prompt bar once user submits
  const [showWelcome, setShowWelcome] = useState(true);
  const [showPromptBar, setShowPromptBar] = useState(true);

  // Bubbles
  const [bubbles, setBubbles] = useState([]);

  // Cursor
  const [cursorPos, setCursorPos] = useState({ x: -50, y: -50 });

  // Gallery
  const [galleryImages, setGalleryImages] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [modalImage, setModalImage] = useState(null);

  /********************************************
   *  TRACK MOUSE for color shift + dynamic cursor
   ********************************************/
  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPos({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  // A hue shift based on cursor X to color "Welcome Jie Hao"
  function getWelcomeHue() {
    const w = window.innerWidth || 1;
    const ratio = cursorPos.x / w;
    return Math.floor(ratio * 360);
  }

  /********************************************
   *  BACKEND: GALLERY
   ********************************************/
  const fetchGallery = async () => {
    try {
      const res = await fetch("http://localhost:8080/gallery");
      const data = await res.json();
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  useEffect(() => {
    // fetch gallery on mount and after generating/canceling
    fetchGallery();
  }, [loading]);

  /********************************************
   *  BUBBLES: random suggestions appear & vanish
   ********************************************/
  useEffect(() => {
    const spawnInterval = setInterval(() => {
      const suggestion = allSuggestions[Math.floor(Math.random() * allSuggestions.length)];
      const { x, y } = getRandomPos();
      const id = `bub_${Date.now()}_${Math.random().toFixed(5)}`;

      const newBubble = { id, text: suggestion, x, y };
      setBubbles((prev) => [...prev, newBubble]);

      // auto-remove after 5s
      setTimeout(() => {
        setBubbles((curr) => curr.filter((b) => b.id !== id));
      }, 5000);
    }, 3000); // spawn every 3 seconds

    return () => clearInterval(spawnInterval);
  }, []);

  function onBubbleClick(bubble) {
    // Add to prompt
    setPrompt((prev) => (prev ? prev + " " + bubble.text : bubble.text));
    // remove bubble
    setBubbles((curr) => curr.filter((b) => b.id !== bubble.id));
  }

  /********************************************
   *  BACKEND: PROGRESS, GENERATION, CANCEL
   ********************************************/
  function pollProgress(selectedModel) {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) {
          clearInterval(interval);
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(interval);
      }
    }, 1000);
  }

  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setEta(0);
    setImageUrl("");
    setImageError(false);
    setLoading(true);

    // fade out welcome + prompt bar
    setShowWelcome(false);
    setShowPromptBar(false);

    if (model !== "dalle") {
      pollProgress(model);
    }

    try {
      const res = await fetch("http://localhost:8080/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt, model }),
      });
      if (!res.ok) throw new Error(`Server error: ${res.status}`);

      const data = await res.json();
      setImageUrl(data.imageUrl);
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async () => {
    await fetch("http://localhost:8080/cancel", { method: "POST" });
    setLoading(false);
    setProgress(0);
    setEta(0);

    // re-show the welcome + prompt bar
    setShowWelcome(true);
    setShowPromptBar(true);

    setImageUrl("");
    setImageError(false);
  };

  const handleRegenerate = () => {
    setPrompt("");
    setImageUrl("");
    setImageError(false);

    // re-show welcome + prompt bar
    setShowWelcome(true);
    setShowPromptBar(true);
    setProgress(0);
    setEta(0);
  };

  // If the backend returns a relative URL for local images, prefix it
  // If it's dall-e with absolute, keep it
  function getFullImageUrl(url) {
    if (!url) return "";
    if (url.startsWith("http")) return url;
    return `http://localhost:8080${url}`;
  }

  /********************************************
   *  SIDEBAR & MODAL GALLERY
   ********************************************/
  function toggleSidebar() {
    setSidebarOpen(!sidebarOpen);
  }
  function onGalleryImageClick(url) {
    setModalImage(url);
  }
  function closeModal() {
    setModalImage(null);
  }

  /********************************************
   *  DARK MODE
   ********************************************/
  useEffect(() => {
    if (darkMode) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
  }, [darkMode]);

  /********************************************
   *  RENDER
   ********************************************/
  const welcomeHue = getWelcomeHue();

  // Are we done generating? (Do we have an image?)
  const hasImage = !!imageUrl;

  return (
    <div className="app-container">
      {/* 1) WELCOME MESSAGE (center, with color shift). fade out if showWelcome=false */}
      {showWelcome && (
        <div
          className={`welcome-message ${showWelcome ? "fade-in" : "fade-out"}`}
          style={{ color: `hsl(${welcomeHue}, 80%, 60%)` }}
        >
          Welcome Jie Hao
        </div>
      )}

      {/* 7) Cursor follower */}
      <div
        className="cursor-follower"
        style={{ transform: `translate(${cursorPos.x}px, ${cursorPos.y}px)` }}
      ></div>

      {/* 5) Floating Bubbles (Prompts) */}
      {bubbles.map((b) => (
        <div
          key={b.id}
          className="floating-bubble"
          style={{ top: `${b.y}vh`, left: `${b.x}vw` }}
          onClick={() => onBubbleClick(b)}
        >
          {b.text}
        </div>
      ))}

      {/* 6) If loading: show overlay with progress/cancel */}
      {loading && (
        <div className="loading-overlay">
          <div className="loading-container">
            {model === "dalle" ? (
              <>
                <div className="spinner">
                  <div className="loader"></div>
                  <p>Loading... (estimated 30 seconds)</p>
                </div>
                <button className="cancel-large" onClick={handleCancel}>
                  X
                </button>
              </>
            ) : (
              <>
                <progress value={progress} max="100" className="progress-bar"></progress>
                <div className="progress-info">
                  <span className="progress-text">{progress}%</span>
                  <span className="eta-text">ETA: {eta} sec</span>
                </div>
                <button className="cancel-large" onClick={handleCancel}>
                  X
                </button>
              </>
            )}
          </div>
        </div>
      )}

      {/* 2) PROMPT BAR: 5cm below the welcome message. fade out on submit */}
      {!loading && !hasImage && showPromptBar && (
        <form
          onSubmit={handleGenerate}
          className="prompt-bar"
        >
          {/* Model selector */}
          <div className="model-dropdown">
            <button type="button" className="model-button">Model</button>
            <div className="model-dropdown-content">
              <div onClick={() => setModel("model1")}>Asian Model</div>
              <div onClick={() => setModel("model2")}>Anime Model</div>
              <div onClick={() => setModel("dalle")}>DALLÂ·E</div>
            </div>
          </div>

          {/* Prompt input (fixed ~10cm width) */}
          <input
            type="text"
            className="prompt-input-box"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your prompt..."
            required
          />

          {/* Action button (arrow) */}
          <button type="submit" className="action-button">
            â†’
          </button>
        </form>
      )}

      {/* If we have an image -> show final display & "New Prompt" below it */}
      {!loading && hasImage && (
        <div className="final-display">
          {imageError ? (
            <p className="error-text">Image generation failed or could not be loaded.</p>
          ) : (
            <div className="generated-image-container">
              <img
                className="generated-image"
                src={getFullImageUrl(imageUrl)}
                alt="Generated"
                onError={() => setImageError(true)}
              />
            </div>
          )}
          <button className="new-prompt-button" onClick={handleRegenerate}>
            New Prompt
          </button>
        </div>
      )}

      {/* 3) Sidebar with bigger gallery */}
      <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
        <button className="gallery-icon" onClick={toggleSidebar}>
          ðŸ–¼
        </button>
        {sidebarOpen && (
          <div className="sidebar-gallery">
            <h3>Gallery</h3>
            <div className="sidebar-gallery-list">
              {galleryImages.length > 0 ? (
                galleryImages.map((url, idx) => (
                  <img
                    key={idx}
                    src={getFullImageUrl(url)}
                    alt={`gallery_${idx}`}
                    className="sidebar-gallery-img"
                    onClick={() => setModalImage(url)}
                  />
                ))
              ) : (
                <p>No images yet...</p>
              )}
            </div>
          </div>
        )}
      </div>

      {/* 4) Modal if user clicks an image in the gallery */}
      {modalImage && (
        <div className="image-modal" onClick={closeModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-modal" onClick={closeModal}>
              Ã—
            </button>
            <img
              src={getFullImageUrl(modalImage)}
              alt="modal"
              className="modal-img"
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
