import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const initialSuggestions = [
  "sunset", "mountains", "forest", "cityscape", "abstract", 
  "vintage", "futuristic", "space", "cyberpunk", "waterfall",
  "beach", "night sky", "aurora", "rainforest", "desert",
  "ocean", "galaxy", "minimal", "vibrant", "surreal"
];

function initializeBubbles() {
  const now = Date.now();
  const shuffled = [...initialSuggestions].sort(() => 0.5 - Math.random());
  const visible = shuffled.slice(0, 5).map((text, index) => ({
    id: index,
    text,
    top: Math.random() * 150 + 20,
    left: Math.random() * 600 + 20,
    duration: Math.random() * 5 + 5,
    popped: false,
    createdAt: now,
  }));
  const standby = shuffled.slice(5).map((text, index) => ({
    id: index + 5,
    text,
    top: Math.random() * 150 + 20,
    left: Math.random() * 600 + 20,
    duration: Math.random() * 5 + 5,
    popped: false,
    createdAt: now,
  }));
  return { visible, standby };
}

function App() {
  // Core state variables.
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [showModelDropdown, setShowModelDropdown] = useState(false);
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [cursorPos, setCursorPos] = useState({ x: -50, y: -50 });
  const [visibleBubbles, setVisibleBubbles] = useState([]);
  const [standbyBubbles, setStandbyBubbles] = useState([]);
  const [galleryImages, setGalleryImages] = useState([]);
  const [expandedGalleryImage, setExpandedGalleryImage] = useState(null);
  const [showPromptBar, setShowPromptBar] = useState(true);
  // Added state for gallery modal (triggered via sidebar button).
  const [showGalleryModal, setShowGalleryModal] = useState(false);

  // For drag-and-drop of bubbles.
  const [draggingBubble, setDraggingBubble] = useState(null);
  const [dragStart, setDragStart] = useState(null);
  const bubblesContainerRef = useRef(null);

  // Initialize bubbles on mount.
  useEffect(() => {
    const { visible, standby } = initializeBubbles();
    setVisibleBubbles(visible);
    setStandbyBubbles(standby);
  }, []);

  // Remove bubbles older than 5 seconds and fill from standby.
  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      setVisibleBubbles((prev) => {
        const filtered = prev.filter(b => now - b.createdAt < 5000);
        while (filtered.length < 5 && standbyBubbles.length > 0) {
          const newBubble = { ...standbyBubbles[0], createdAt: now };
          setStandbyBubbles(prevStandby => prevStandby.slice(1));
          filtered.push(newBubble);
        }
        return filtered;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [standbyBubbles]);

  // Fetch gallery images from backend.
  const fetchGallery = async () => {
    try {
      const res = await fetch('http://localhost:8080/gallery');
      const data = await res.json();
      console.log("Fetched gallery images:", data.images);
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  // Refresh gallery after each generation.
  useEffect(() => {
    fetchGallery();
  }, [loading]);

  // Dynamic cursor follower.
  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPos({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Global dragging handlers.
  useEffect(() => {
    function handleMouseMove(e) {
      if (draggingBubble && bubblesContainerRef.current) {
        const containerRect = bubblesContainerRef.current.getBoundingClientRect();
        setVisibleBubbles((prev) =>
          prev.map((b) =>
            b.id === draggingBubble.id
              ? {
                  ...b,
                  left: e.clientX - containerRect.left - draggingBubble.offsetX,
                  top: e.clientY - containerRect.top - draggingBubble.offsetY,
                }
              : b
          )
        );
      }
    }
    function handleMouseUp(e) {
      if (draggingBubble && dragStart) {
        const dx = e.clientX - dragStart.startX;
        const dy = e.clientY - dragStart.startY;
        const dist = Math.sqrt(dx * dx + dy * dy);
        if (dist < 5) {
          handleBubbleClick(draggingBubble.id);
        }
      }
      setDraggingBubble(null);
      setDragStart(null);
    }
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [draggingBubble, dragStart]);

  const handleBubbleMouseDown = (e, bubble) => {
    e.preventDefault();
    const rect = e.currentTarget.getBoundingClientRect();
    const offsetX = e.clientX - rect.left;
    const offsetY = e.clientY - rect.top;
    setDraggingBubble({ id: bubble.id, offsetX, offsetY });
    setDragStart({ id: bubble.id, startX: e.clientX, startY: e.clientY });
  };

  const handleBubbleClick = (bubbleId) => {
    const bubble = visibleBubbles.find(b => b.id === bubbleId);
    if (!bubble) return;
    setPrompt(prev => prev + (prev ? " " : "") + bubble.text);
    setVisibleBubbles(prev => prev.map(b => b.id === bubbleId ? { ...b, popped: true } : b));
    setTimeout(() => {
      setVisibleBubbles(prev => prev.filter(b => b.id !== bubbleId));
      if (standbyBubbles.length > 0) {
        const newBubble = { ...standbyBubbles[0], createdAt: Date.now() };
        setStandbyBubbles(prev => prev.slice(1));
        setVisibleBubbles(prev => [...prev, newBubble]);
      }
    }, 300);
  };

  // Poll backend for progress updates.
  const pollProgress = (selectedModel) => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) clearInterval(interval);
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(interval);
      }
    }, 1000);
  };

  const handleGenerate = async (e) => {
    if (e && e.preventDefault) e.preventDefault();
    // When generating, hide the prompt bar (including welcome).
    setShowPromptBar(false);
    // Also clear floating bubbles.
    setVisibleBubbles([]);
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setImageError(false);
    setLoading(true);
    if (model !== 'dalle') {
      pollProgress(model);
    }
    try {
      const response = await fetch('http://localhost:8080/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, model }),
      });
      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }
      const data = await response.json();
      setImageUrl(data.imageUrl);
      fetchGallery();
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async () => {
    await fetch('http://localhost:8080/cancel', { method: 'POST' });
    // Fully reset UI.
    setLoading(false);
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setPrompt('');
    setShowPromptBar(true);
  };

  const handleRegenerate = () => {
    // Clear the current generated image and reset the prompt bar.
    setPrompt('');
    setImageUrl('');
    setProgress(0);
    setEta(0);
    setImageError(false);
    const { visible, standby } = initializeBubbles();
    setVisibleBubbles(visible);
    setStandbyBubbles(standby);
    setShowPromptBar(true);
  };

  // Model dropdown handlers.
  const toggleModelDropdown = () => setShowModelDropdown(!showModelDropdown);
  const selectModel = (selected) => {
    setModel(selected);
    setShowModelDropdown(false);
  };

  const toggleDarkMode = () => setDarkMode(!darkMode);

  useEffect(() => {
    darkMode
      ? document.body.classList.add('dark-mode')
      : document.body.classList.remove('dark-mode');
  }, [darkMode]);

  // Ensure imageUrl is absolute.
  const getImageUrl = () => {
    if (imageUrl.startsWith("http")) return imageUrl;
    return `http://localhost:8080${imageUrl}`;
  };

  // For Enter key in prompt input.
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !loading && !imageUrl) {
      handleGenerate(e);
    }
  };

  // Render main action button: arrow when idle, cancel (X) when loading, New Prompt when image is shown.
  const renderActionButton = () => {
    if (imageUrl) {
      return (
        <button type="button" onClick={handleRegenerate} className="action-button new-prompt">
          New Prompt
        </button>
      );
    }
    if (loading) {
      return (
        <button type="button" onClick={handleCancel} className="action-button cancel">
          ✖
        </button>
      );
    }
    return (
      <button type="button" onClick={handleGenerate} className="action-button generate">
        ➔
      </button>
    );
  };

  // Gallery Modal handlers.
  const handleGalleryImageClick = (imgUrl) => {
    setExpandedGalleryImage(imgUrl);
  };
  const closeGalleryModal = () => {
    setExpandedGalleryImage(null);
    setShowGalleryModal(false);
  };

  return (
    <div className="wrapper">
      {/* Sidebar with Gallery Button */}
      <div className="sidebar">
        <button className="gallery-btn" onClick={() => setShowGalleryModal(true)}>
          <i className="bx bx-image"></i>
        </button>
      </div>

      <div className="app-container">
        {/* Welcome Message & Prompt Bar */}
        {showPromptBar && (
          <>
            <div className="welcome-message" style={{ backgroundPosition: `${cursorPos.x}px ${cursorPos.y}px` }}>
              <h1>Welcome Jie Hao</h1>
            </div>
            <div className="prompt-bar">
              <div className="model-selector">
                <button className="model-button" onClick={toggleModelDropdown}>
                  {model === "model1" ? "Asian Model" : model === "model2" ? "Anime Model" : "DALL·E"}
                  <span className="dropdown-arrow">▾</span>
                </button>
                {showModelDropdown && (
                  <div className="model-dropdown">
                    <div onClick={() => selectModel("model1")}>Asian Model</div>
                    <div onClick={() => selectModel("model2")}>Anime Model</div>
                    <div onClick={() => selectModel("dalle")}>DALL·E</div>
                  </div>
                )}
              </div>
              <input
                type="text"
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                placeholder="Enter your prompt..."
                onKeyDown={handleKeyDown}
                className="prompt-input-main"
              />
              {renderActionButton()}
            </div>
          </>
        )}

        {/* Floating Bubbles */}
        <div className="bubbles-container" ref={bubblesContainerRef}>
          {visibleBubbles.map(bubble => (
            <div
              key={bubble.id}
              className={`bubble ${bubble.popped ? 'pop' : ''}`}
              style={{
                top: `${bubble.top}px`,
                left: `${bubble.left}px`,
                animationDuration: `${bubble.duration}s`
              }}
              onMouseDown={(e) => handleBubbleMouseDown(e, bubble)}
            >
              {bubble.text}
            </div>
          ))}
        </div>

        {/* Loading / Progress */}
        {loading && (
          <div className="loading-container">
            {model === "dalle" ? (
              <div className="spinner">
                <div className="loader"></div>
                <p>Loading... (estimated 30 seconds)</p>
              </div>
            ) : (
              <div className="progress-container">
                <progress value={progress} max="100" className="progress-bar"></progress>
                <div className="progress-info">
                  <span className="progress-text">{progress}%</span>
                  <span className="eta-text">ETA: {eta} sec</span>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Generated Image Display */}
        {imageUrl && (
          <div className="image-container">
            <h2>Generated Image:</h2>
            {imageError ? (
              <p className="error-text">Image generation failed or could not be loaded.</p>
            ) : (
              <img
                src={getImageUrl()}
                alt="Generated"
                className="generated-image"
                onError={() => setImageError(true)}
              />
            )}
          </div>
        )}
      </div>

      {/* Gallery Modal */}
      {showGalleryModal && (
        <div className="gallery-modal" onClick={closeGalleryModal}>
          <div className="gallery-modal-content" onClick={e => e.stopPropagation()}>
            <button className="close-gallery" onClick={closeGalleryModal}>&times;</button>
            <h2>Gallery</h2>
            <div className="gallery-grid">
              {galleryImages.length > 0 ? (
                galleryImages.map((imgUrl, idx) => (
                  <img
                    key={idx}
                    src={imgUrl}
                    alt={`Gallery ${idx}`}
                    className="gallery-item"
                    onClick={() => handleGalleryImageClick(imgUrl)}
                  />
                ))
              ) : (
                <p>No images available.</p>
              )}
            </div>
            {expandedGalleryImage && (
              <div className="expanded-gallery-modal" onClick={closeGalleryModal}>
                <div className="expanded-gallery-content" onClick={e => e.stopPropagation()}>
                  <button className="close-expanded" onClick={closeGalleryModal}>&times;</button>
                  <img src={expandedGalleryImage} alt="Expanded Gallery" className="expanded-gallery-image" />
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Fluid Dynamic Cursor Follower */}
      <div
        className="cursor-follower"
        style={{ transform: `translate(${cursorPos.x}px, ${cursorPos.y}px)` }}
      ></div>
    </div>
  );
}

export default App;
