import React, { useState, useEffect, useRef } from 'react';
import './App.css';

/////////////////////////////////////////////////
// HELPER: Suggestions for the floating bubbles //
/////////////////////////////////////////////////
const initialSuggestions = [
  "sunset", "mountains", "forest", "cityscape", "abstract",
  "vintage", "futuristic", "space", "cyberpunk", "waterfall",
  "beach", "night sky", "aurora", "rainforest", "desert",
  "ocean", "galaxy", "minimal", "vibrant", "surreal"
];

// Generate a random position on the screen for the bubble
function randomPosition() {
  // random X between 10% and 80% of the viewport width
  // random Y between 10% and 80% of the viewport height
  const x = Math.random() * 70 + 10;
  const y = Math.random() * 70 + 10;
  return { x, y };
}

/////////////////////////////////////////////////
// MAIN APP COMPONENT
/////////////////////////////////////////////////
function App() {
  // Core states
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);

  // Bubbles logic
  const [bubbles, setBubbles] = useState([]);
  const bubbleTimeouts = useRef({}); // Track timeouts for bubble removal

  // Cursor and color effect states
  const [cursorPos, setCursorPos] = useState({ x: -50, y: -50 });
  const [galleryImages, setGalleryImages] = useState([]);
  const [showModalImage, setShowModalImage] = useState(null); // For image modal
  const [sidebarOpen, setSidebarOpen] = useState(false);       // For gallery sidebar

  //////////////////////////////////////////////////
  // 1. WELCOME MESSAGE ANIMATION + CURSOR TRACKING
  //////////////////////////////////////////////////
  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPos({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // The hue can shift based on cursor X position
  // so we do something like: hue = (cursorPos.x / windowWidth) * 360
  function getWelcomeHue() {
    const w = window.innerWidth || 1;
    const ratio = cursorPos.x / w;
    return Math.floor(ratio * 360);
  }

  /////////////////////////////////////////////////
  // 2. FETCH GALLERY IMAGES (same as your backend)
  /////////////////////////////////////////////////
  const fetchGallery = async () => {
    try {
      const res = await fetch('http://localhost:8080/gallery');
      const data = await res.json();
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  // Fetch gallery once on mount and after generating/canceling
  useEffect(() => {
    fetchGallery();
  }, [loading]);

  /////////////////////////////////////////////////
  // 3. BUBBLES: random suggestions + animations
  /////////////////////////////////////////////////
  // On mount, let's periodically spawn bubbles
  useEffect(() => {
    const spawnInterval = setInterval(() => {
      // Randomly pick a suggestion
      const suggestion = initialSuggestions[
        Math.floor(Math.random() * initialSuggestions.length)
      ];
      // Random positions
      const { x, y } = randomPosition();
      const id = Date.now() + '_' + Math.floor(Math.random() * 1000);

      // Create a new bubble
      const newBubble = {
        id,
        text: suggestion,
        x,
        y,
      };

      setBubbles(prev => [...prev, newBubble]);

      // Remove the bubble after ~5 seconds
      const timeout = setTimeout(() => {
        setBubbles(current => current.filter(b => b.id !== id));
      }, 5000);
      bubbleTimeouts.current[id] = timeout;
    }, 3000); // spawn a bubble every 3s or so

    return () => {
      clearInterval(spawnInterval);
      // clear all timeouts
      Object.values(bubbleTimeouts.current).forEach(t => clearTimeout(t));
    };
  }, []);

  // When a bubble is clicked, "fly" the text into the prompt
  function handleBubbleClick(bubble) {
    // Add bubble text to prompt
    setPrompt(prev => (prev ? prev + ' ' + bubble.text : bubble.text));

    // Animate bubble out
    setBubbles(prev => prev.filter(b => b.id !== bubble.id));
  }

  /////////////////////////////////////////////////
  // 4. BACKEND LOGIC: GENERATE, CANCEL, PROGRESS
  /////////////////////////////////////////////////
  function pollProgress(selectedModel) {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) {
          clearInterval(interval);
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(interval);
      }
    }, 1000);
  }

  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setImageError(false);
    setLoading(true);

    // For local models, start progress polling
    if (model !== 'dalle') {
      pollProgress(model);
    }
    try {
      const response = await fetch('http://localhost:8080/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, model }),
      });
      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }
      const data = await response.json();
      setImageUrl(data.imageUrl);
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async () => {
    await fetch('http://localhost:8080/cancel', { method: 'POST' });
    setLoading(false);
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setImageError(false);
  };

  const handleRegenerate = () => {
    setPrompt('');
    setImageUrl('');
    setProgress(0);
    setEta(0);
    setImageError(false);
  };

  // Return an absolute URL if necessary
  function getFullImageUrl(url) {
    if (!url) return '';
    if (url.startsWith('http')) return url;
    return `http://localhost:8080${url}`;
  }

  /////////////////////////////////////////////////
  // 5. GALLERY SIDEBAR & MODAL
  /////////////////////////////////////////////////
  function toggleSidebar() {
    setSidebarOpen(!sidebarOpen);
  }

  function handleImageClickInGallery(url) {
    setShowModalImage(url);
  }
  function closeModal() {
    setShowModalImage(null);
  }

  /////////////////////////////////////////////////
  // 6. STYLING & RENDER
  /////////////////////////////////////////////////
  useEffect(() => {
    if (darkMode) document.body.classList.add('dark-mode');
    else document.body.classList.remove('dark-mode');
  }, [darkMode]);

  // If the user has generated an image, the prompt bar should be replaced by loading or new prompt
  const hasGenerated = !!imageUrl;

  // Arrow or X is the "action button"
  // If not generating, show arrow; if generating, show X
  // If generated, show "New Prompt" or re-check logic above
  const hue = getWelcomeHue();

  return (
    <div className="app-container">
      {/* 1. WELCOME MESSAGE in center (with color shifting) */}
      <div className="welcome-message" style={{ color: `hsl(${hue}, 80%, 60%)` }}>
        Welcome Jie Hao
      </div>

      {/* 7. DYNAMIC CURSOR FOLLOWER */}
      <div
        className="cursor-follower"
        style={{ transform: `translate(${cursorPos.x}px, ${cursorPos.y}px)` }}
      ></div>

      {/* 3. FLOATING BUBBLES ACROSS SCREEN */}
      {bubbles.map((b) => (
        <div
          key={b.id}
          className="floating-bubble"
          style={{
            top: b.y + 'vh',
            left: b.x + 'vw',
          }}
          onClick={() => handleBubbleClick(b)}
        >
          {b.text}
        </div>
      ))}

      {/* 4. LOADING & CANCEL / Progress (center if loading) */}
      {loading && (
        <div className="loading-overlay">
          <div className="loading-container">
            {model === 'dalle' ? (
              <>
                <div className="spinner">
                  <div className="loader"></div>
                  <p>Loading... (estimated 30 seconds)</p>
                </div>
                <button className="cancel-large" onClick={handleCancel}>X</button>
              </>
            ) : (
              <>
                <progress value={progress} max="100" className="progress-bar"></progress>
                <div className="progress-info">
                  <span className="progress-text">{progress}%</span>
                  <span className="eta-text">ETA: {eta} sec</span>
                </div>
                <button className="cancel-large" onClick={handleCancel}>X</button>
              </>
            )}
          </div>
        </div>
      )}

      {/* 2. PROMPT BAR (BOTTOM) - only if not loading */}
      {!loading && !hasGenerated && (
        <form onSubmit={handleGenerate} className="prompt-bar">
          {/* Model selector button */}
          <div className="model-dropdown">
            <button type="button" className="model-button">
              Model
            </button>
            <div className="model-dropdown-content">
              <div onClick={() => setModel('model1')}>Asian Model</div>
              <div onClick={() => setModel('model2')}>Anime Model</div>
              <div onClick={() => setModel('dalle')}>DALL·E</div>
            </div>
          </div>

          {/* Prompt input box */}
          <input
            type="text"
            className="prompt-input-box"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your prompt..."
            required
          />

          {/* Arrow icon to submit */}
          <button type="submit" className="action-button">
            →
          </button>
        </form>
      )}

      {/* If generation is done, show the "New Prompt" action */}
      {!loading && hasGenerated && (
        <div className="after-generate-bar">
          {imageError ? (
            <p className="error-text">
              Image generation failed or could not be loaded.
            </p>
          ) : (
            <div className="image-container">
              <h2>Generated Image:</h2>
              <img
                src={getFullImageUrl(imageUrl)}
                alt="Generated"
                className="generated-image"
                onError={() => setImageError(true)}
              />
            </div>
          )}
          <button className="new-prompt-button" onClick={handleRegenerate}>
            New Prompt
          </button>
        </div>
      )}

      {/* 3. SIDEBAR (Left Edge) with gallery button */}
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <button className="gallery-icon" onClick={toggleSidebar}>
          {/* Could use an SVG icon or emoji for the pic icon */}
          🖼
        </button>

        {/* If sidebar is open, show the images */}
        {sidebarOpen && (
          <div className="sidebar-gallery">
            <h3>Gallery</h3>
            <div className="sidebar-gallery-list">
              {galleryImages.length > 0 ? (
                galleryImages.map((url, idx) => (
                  <img
                    key={idx}
                    src={getFullImageUrl(url)}
                    alt={`gallery_${idx}`}
                    className="sidebar-gallery-img"
                    onClick={() => handleImageClickInGallery(url)}
                  />
                ))
              ) : (
                <p>No images yet...</p>
              )}
            </div>
          </div>
        )}
      </div>

      {/* 5. GALLERY IMAGE MODAL (when an image is clicked in sidebar) */}
      {showModalImage && (
        <div className="image-modal" onClick={closeModal}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-modal" onClick={closeModal}>×</button>
            <img
              src={getFullImageUrl(showModalImage)}
              alt="modal"
              className="modal-img"
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
