import React, { useState } from 'react';
import './ChatBot.css';

const ChatBot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Toggle chatbox open/close
  const toggleChat = () => setIsOpen((prev) => !prev);

  // Handle sending message and receiving response from Gemini API
  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = { sender: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      // Replace '/api/gemini' with your actual Gemini API endpoint and include any auth headers if needed
      const response = await fetch('http://localhost:8080/api/gemini', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // 'Authorization': 'Bearer YOUR_GEMINI_API_KEY', // if required
        },
        body: JSON.stringify({ prompt: input }),
      });
      const data = await response.json();
      const aiMessage = { sender: 'ai', text: data.response };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error(err);
      const errorMessage = {
        sender: 'ai',
        text: 'Sorry, there was an error processing your request.',
      };
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  // Send message on pressing Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="chatbot-container">
      {isOpen ? (
        <div className="chatbox">
          <div className="chatbox-header">
            <h4>AI Assistant</h4>
            <button onClick={toggleChat} className="close-button">
              &times;
            </button>
          </div>
          <div className="chatbox-body">
            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
            {loading && <div className="message ai">Typing...</div>}
          </div>
          <div className="chatbox-footer">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your message..."
            />
            <button onClick={sendMessage} className="send-button">
              Send
            </button>
          </div>
        </div>
      ) : (
        <button onClick={toggleChat} className="chatbot-toggle-button">
          Chat
        </button>
      )}
    </div>
  );
};

export default ChatBot;
