import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const initialSuggestions = [
  "sunset", "mountains", "forest", "cityscape", "abstract", 
  "vintage", "futuristic", "space", "cyberpunk", "waterfall",
  "beach", "night sky", "aurora", "rainforest", "desert",
  "ocean", "galaxy", "minimal", "vibrant", "surreal"
];

function initializeBubbles() {
  const shuffled = [...initialSuggestions].sort(() => 0.5 - Math.random());
  const visible = shuffled.slice(0, 5).map((text, index) => ({
    id: index,
    text,
    top: Math.random() * 150 + 20,
    left: Math.random() * 600 + 20,
    duration: Math.random() * 5 + 5,
    popped: false,
  }));
  const standby = shuffled.slice(5).map((text, index) => ({
    id: index + 5,
    text,
    top: Math.random() * 150 + 20,
    left: Math.random() * 600 + 20,
    duration: Math.random() * 5 + 5,
    popped: false,
  }));
  return { visible, standby };
}

function App() {
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [cursorPos, setCursorPos] = useState({ x: -50, y: -50 });
  const [visibleBubbles, setVisibleBubbles] = useState([]);
  const [standbyBubbles, setStandbyBubbles] = useState([]);
  const [draggingBubble, setDraggingBubble] = useState(null);
  const [dragStart, setDragStart] = useState(null);
  const [galleryImages, setGalleryImages] = useState([]);
  const [showWelcome, setShowWelcome] = useState(true);
  const bubblesContainerRef = useRef(null);
  const promptBarRef = useRef(null);

  // Show welcome message on load.
  useEffect(() => {
    const { visible, standby } = initializeBubbles();
    setVisibleBubbles(visible);
    setStandbyBubbles(standby);
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => setShowWelcome(false), 4000);
    return () => clearTimeout(timer);
  }, []);

  // Dynamic color for welcome message based on cursor position.
  const welcomeColor = `rgb(${cursorPos.x % 256}, ${cursorPos.y % 256}, 150)`;

  // Fetch gallery images from backend.
  const fetchGallery = async () => {
    try {
      const res = await fetch('http://localhost:8080/gallery');
      const data = await res.json();
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  // Refresh gallery images on mount and after generation.
  useEffect(() => {
    fetchGallery();
  }, [loading]);

  // Update dynamic cursor position.
  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPos({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Global dragging handlers for bubbles.
  useEffect(() => {
    function handleMouseMove(e) {
      if (draggingBubble && bubblesContainerRef.current) {
        const containerRect = bubblesContainerRef.current.getBoundingClientRect();
        setVisibleBubbles((prev) =>
          prev.map((b) =>
            b.id === draggingBubble.id
              ? { 
                  ...b, 
                  left: e.clientX - containerRect.left - draggingBubble.offsetX, 
                  top: e.clientY - containerRect.top - draggingBubble.offsetY 
                }
              : b
          )
        );
      }
    }
    function handleMouseUp(e) {
      if (draggingBubble && dragStart) {
        const dx = e.clientX - dragStart.startX;
        const dy = e.clientY - dragStart.startY;
        const dist = Math.sqrt(dx * dx + dy * dy);
        if (dist < 5) {
          handleBubbleClick(draggingBubble.id);
        }
      }
      setDraggingBubble(null);
      setDragStart(null);
    }
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [draggingBubble, dragStart]);

  const handleBubbleMouseDown = (e, bubble) => {
    e.preventDefault();
    const targetRect = e.currentTarget.getBoundingClientRect();
    const offsetX = e.clientX - targetRect.left;
    const offsetY = e.clientY - targetRect.top;
    setDraggingBubble({ id: bubble.id, offsetX, offsetY });
    setDragStart({ id: bubble.id, startX: e.clientX, startY: e.clientY });
  };

  const handleBubbleClick = (bubbleId) => {
    const bubble = visibleBubbles.find(b => b.id === bubbleId);
    if (!bubble) return;
    // Add fly-to effect via CSS class (handled by .fly-to in CSS if desired)
    setVisibleBubbles(prev => prev.map(b => b.id === bubbleId ? { ...b, popped: true } : b));
    setPrompt(prev => prev + (prev ? " " : "") + bubble.text);
    setTimeout(() => {
      setVisibleBubbles(prev => prev.filter(b => b.id !== bubbleId));
      if (standbyBubbles.length > 0) {
        const newBubble = standbyBubbles[0];
        setStandbyBubbles(prev => prev.slice(1));
        setVisibleBubbles(prev => [...prev, newBubble]);
      }
    }, 300);
  };

  const pollProgress = (selectedModel) => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) clearInterval(interval);
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(interval);
      }
    }, 1000);
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setEta(0);
    setImageUrl('');
    setImageError(false);
    setLoading(true);
    if (model !== 'dalle') {
      pollProgress(model);
    }
    try {
      const response = await fetch('http://localhost:8080/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, model }),
      });
      if (!response.ok) throw new Error(`Server error: ${response.status}`);
      const data = await response.json();
      setImageUrl(data.imageUrl);
      fetchGallery();
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async () => {
    await fetch('http://localhost:8080/cancel', { method: 'POST' });
    setLoading(false);
    setProgress(0);
    setEta(0);
  };

  const handleRegenerate = () => {
    setPrompt('');
    setImageUrl('');
    setProgress(0);
    setEta(0);
    setImageError(false);
    const { visible, standby } = initializeBubbles();
    setVisibleBubbles(visible);
    setStandbyBubbles(standby);
  };

  const toggleDarkMode = () => setDarkMode(!darkMode);

  useEffect(() => {
    darkMode
      ? document.body.classList.add('dark-mode')
      : document.body.classList.remove('dark-mode');
  }, [darkMode]);

  // Ensure absolute URL for image.
  const getImageUrl = () => {
    return imageUrl.startsWith("http") ? imageUrl : `http://localhost:8080${imageUrl}`;
  };

  return (
    <div className="app-container">
      {showWelcome && (
        <div className="welcome-message" style={{ color: welcomeColor }}>
          Welcome Jie Hao
        </div>
      )}

      {/* Sidebar with Gallery Button Icon */}
      <div className="sidebar">
        <button className="gallery-sidebar-button">
          <span role="img" aria-label="gallery">🖼️</span>
        </button>
      </div>

      {/* Main Content Area */}
      <div className="content-area">
        {loading && (
          <div className="loading-container">
            {model === "dalle" ? (
              <div className="spinner">
                <div className="loader"></div>
                <p>Loading... (estimated 30 seconds)</p>
              </div>
            ) : (
              <div className="progress-container">
                <progress value={progress} max="100" className="progress-bar"></progress>
                <div className="progress-info">
                  <span className="progress-text">{progress}%</span>
                  <span className="eta-text">ETA: {eta} sec</span>
                </div>
              </div>
            )}
          </div>
        )}
        {imageUrl && (
          <div className="image-container">
            <h2>Generated Image:</h2>
            {imageError ? (
              <p className="error-text">Image generation failed or could not be loaded.</p>
            ) : (
              <img
                src={getImageUrl()}
                alt="Generated"
                className="generated-image"
                onError={() => setImageError(true)}
              />
            )}
          </div>
        )}
        <div className="bubbles-container" ref={bubblesContainerRef}>
          {visibleBubbles.map(bubble => (
            <div
              key={bubble.id}
              className={`bubble ${bubble.popped ? 'pop' : ''}`}
              style={{
                top: `${bubble.top}px`,
                left: `${bubble.left}px`,
                animationDuration: `${bubble.duration}s`
              }}
              onMouseDown={(e) => handleBubbleMouseDown(e, bubble)}
            >
              {bubble.text}
            </div>
          ))}
        </div>
      </div>

      {/* Fixed Prompt Bar at Bottom */}
      {!imageUrl && !loading && (
        <div className="prompt-bar" ref={promptBarRef}>
          <div className="model-selector">
            <select value={model} onChange={(e) => setModel(e.target.value)} className="model-select">
              <option value="model1">Asian Model</option>
              <option value="model2">Anime Model</option>
              <option value="dalle">DALL·E</option>
            </select>
          </div>
          <div className="prompt-input-container">
            <input
              type="text"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="Enter your prompt..."
              className="prompt-input"
            />
          </div>
          <div className="action-button-container">
            <button type="button" onClick={handleGenerate} className="action-button">
              <span className="arrow-icon">&#8594;</span>
            </button>
          </div>
        </div>
      )}
      {imageUrl && !loading && (
        <div className="new-prompt-container">
          <button type="button" onClick={handleRegenerate} className="new-prompt-button">
            New Prompt
          </button>
        </div>
      )}

      {/* Gallery Section: Horizontally scrollable */}
      <div className="gallery-section">
        <h2>Gallery</h2>
        <div className="gallery-scroll">
          {galleryImages.length > 0 ? (
            galleryImages.map((imgUrl, idx) => (
              <img key={idx} src={imgUrl} alt={`Gallery ${idx}`} className="gallery-item" />
            ))
          ) : (
            <p>No images available.</p>
          )}
        </div>
      </div>

      {/* Fluid dynamic cursor follower */}
      <div
        className="cursor-follower"
        style={{ transform: `translate(${cursorPos.x}px, ${cursorPos.y}px)` }}
      ></div>
    </div>
  );
}

export default App;
