import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [model, setModel] = useState('model1');
  const [progress, setProgress] = useState(0);
  const [imageUrl, setImageUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);

  // Poll the backend's /progress endpoint for real progress updates.
  const pollProgress = (selectedModel) => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        if (data.progress >= 100) {
          clearInterval(interval);
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(interval);
      }
    }, 1000);
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setImageUrl('');
    setLoading(true);

    // Start polling progress updates.
    pollProgress(model);

    // Send a POST request to your Golang backend.
    const response = await fetch('http://localhost:8080/generate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt, model }),
    });

    const data = await response.json();
    setImageUrl(data.imageUrl);
    setLoading(false);
  };

  // Reset states so the user can generate a new image with a new prompt.
  const handleRegenerate = () => {
    setPrompt('');
    setImageUrl('');
    setProgress(0);
  };

  // Toggle dark/light mode.
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [darkMode]);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Image Generator</h1>
        <button className="toggle-button" onClick={toggleDarkMode}>
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
      </header>
      <form onSubmit={handleGenerate} className="form-container">
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt..."
          required
          className="prompt-input"
        />
        <select value={model} onChange={(e) => setModel(e.target.value)} className="model-select">
          <option value="model1">Asian Model</option>
          <option value="model2">Anime Model</option>
          <option value="dalle">DALLÂ·E</option>
        </select>
        <div className="button-group">
          <button type="submit" disabled={loading} className="generate-button">
            {loading ? 'Generating...' : 'Generate Image'}
          </button>
          {imageUrl && (
            <button type="button" onClick={handleRegenerate} className="regenerate-button">
              New Prompt
            </button>
          )}
        </div>
      </form>
      {loading && (
        <div className="progress-container">
          <progress value={progress} max="100" className="progress-bar"></progress>
          <span className="progress-text">{progress}%</span>
        </div>
      )}
      {imageUrl && (
        <div className="image-container">
          <h2>Generated Image:</h2>
          {/* Prepend backend URL; your Golang backend serves static files under /images */}
          <img src={`http://localhost:8080${imageUrl}`} alt="Generated" className="generated-image" />
        </div>
      )}
    </div>
  );
}

export default App;
