import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import ChatBot from './ChatBot';

function App() {
  // ---------------------------
  // Application State
  // ---------------------------
  const [currentView, setCurrentView] = useState("create"); // "create" or "gallery"
  const [prompt, setPrompt] = useState("");
  const [model, setModel] = useState("model1");
  const [progress, setProgress] = useState(0);
  const [eta, setEta] = useState(0);
  const [imageUrl, setImageUrl] = useState("");
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [galleryImages, setGalleryImages] = useState([]);
  const [selectedGalleryImage, setSelectedGalleryImage] = useState(null);
  const pollingIntervalRef = useRef(null);

  // ---------------------------
  // Fetch Gallery Images
  // ---------------------------
  const fetchGallery = async () => {
    try {
      const res = await fetch('http://localhost:8080/gallery');
      const data = await res.json();
      console.log("Fetched gallery images:", data.images);
      setGalleryImages(data.images || []);
    } catch (error) {
      console.error("Error fetching gallery:", error);
    }
  };

  useEffect(() => {
    fetchGallery();
  }, [loading]);

  // ---------------------------
  // Progress Polling for Local Models
  // ---------------------------
  const pollProgress = (selectedModel) => {
    if (pollingIntervalRef.current) {
      clearInterval(pollingIntervalRef.current);
    }
    pollingIntervalRef.current = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/progress?model=${selectedModel}`);
        const data = await res.json();
        setProgress(data.progress);
        setEta(data.eta);
        if (data.progress >= 100) {
          clearInterval(pollingIntervalRef.current);
          pollingIntervalRef.current = null;
        }
      } catch (error) {
        console.error("Error polling progress:", error);
        clearInterval(pollingIntervalRef.current);
        pollingIntervalRef.current = null;
      }
    }, 1000);
  };

  // ---------------------------
  // Handle Image Generation
  // ---------------------------
  const handleGenerate = async (e) => {
    e.preventDefault();
    setProgress(0);
    setEta(0);
    setImageUrl("");
    setImageError(false);
    setLoading(true);

    // For non-DALLÂ·E models, start polling progress.
    let progressInterval;
    if (model !== "dalle") {
      progressInterval = pollProgress(model);
    }

    try {
      const response = await fetch('http://localhost:8080/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, model }),
      });
      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }
      const data = await response.json();

      if (progressInterval) {
        clearInterval(progressInterval);
      }
      if (data.imageUrl) {
        setImageUrl(data.imageUrl);
      } else {
        setImageError(true);
      }
      fetchGallery();
    } catch (error) {
      console.error("Error in handleGenerate:", error);
      setImageError(true);
    } finally {
      setLoading(false);
    }
  };

  // ---------------------------
  // Cancel and Reset Generation
  // ---------------------------
  const handleCancel = async () => {
    await fetch('http://localhost:8080/cancel', { method: 'POST' });
    setLoading(false);
    setProgress(0);
    setEta(0);
  };

  const handleRegenerate = () => {
    setPrompt("");
    setImageUrl("");
    setProgress(0);
    setEta(0);
    setImageError(false);
  };

  // ---------------------------
  // Dark Mode Handling
  // ---------------------------
  const toggleDarkMode = () => setDarkMode(!darkMode);

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  // ---------------------------
  // Utility: Get Absolute Image URL with Cache Busting
  // ---------------------------
  const getImageUrl = () => {
    let url = imageUrl.startsWith("http")
      ? imageUrl
      : `http://localhost:8080${imageUrl}`;
    return `${url}?t=${new Date().getTime()}`;
  };

  // ---------------------------
  // Sidebar Component
  // ---------------------------
  const Sidebar = () => {
    return (
      <div className="sidebar">
        <div className="logo">
          <h2>AI Image Creator</h2>
        </div>
        <nav className="nav">
          <button
            className={`nav-button ${currentView === "create" ? "active" : ""}`}
            onClick={() => setCurrentView("create")}
          >
            Create
          </button>
          <button
            className={`nav-button ${currentView === "gallery" ? "active" : ""}`}
            onClick={() => setCurrentView("gallery")}
          >
            Gallery
          </button>
          <button className="nav-button" onClick={toggleDarkMode}>
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </nav>
      </div>
    );
  };

  // ---------------------------
  // Header Component
  // ---------------------------
  const Header = () => {
    return (
      <header className="header">
        <h1>AI Image Creator</h1>
        <p>Unleash Your Creativity with AI-Powered Image Generation</p>
      </header>
    );
  };

  // ---------------------------
  // Creation View Component
  // ---------------------------
  const CreationView = () => {
    return (
      <div className="creation-view">
        <form onSubmit={handleGenerate} className="prompt-form">
          <div className="input-group">
            <select
              value={model}
              onChange={(e) => setModel(e.target.value)}
              className="model-select"
            >
              <option value="model1">Model A</option>
              <option value="model2">Model B</option>
              <option value="dalle">DALLÂ·E</option>
            </select>
            <input
              type="text"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="Enter your prompt here..."
              className="prompt-input"
              required
            />
            <button type="submit" className="generate-button">
              <span className="button-icon">â¨</span> Generate
            </button>
          </div>
        </form>

        {loading && (
          <div className="loading-container">
            {model === "dalle" ? (
              <div className="spinner">
                <div className="loader"></div>
                <p>Loading... (estimated 30 seconds)</p>
              </div>
            ) : (
              <div className="progress-container">
                <progress value={progress} max="100" className="progress-bar"></progress>
                <div className="progress-info">
                  <span>{progress}%</span>
                  <span>ETA: {eta} sec</span>
                </div>
                <button type="button" onClick={handleCancel} className="cancel-button">
                  Cancel
                </button>
              </div>
            )}
          </div>
        )}

        {imageUrl && (
          <div className="image-container">
            <h2>Generated Image:</h2>
            {imageError ? (
              <p className="error-text">
                Image generation failed or could not be loaded.
              </p>
            ) : (
              <img
                src={getImageUrl()}
                alt="Generated"
                className="generated-image"
                onError={() => setImageError(true)}
              />
            )}
            <button onClick={handleRegenerate} className="new-prompt-button">
              New Prompt
            </button>
          </div>
        )}
      </div>
    );
  };

  // ---------------------------
  // Gallery View Component
  // ---------------------------
  const GalleryView = () => {
    return (
      <div className="gallery-view">
        <h2>Gallery</h2>
        {galleryImages.length > 0 ? (
          <div className="gallery-grid">
            {galleryImages.map((imgUrl, idx) => (
              <img
                key={idx}
                src={imgUrl}
                alt={`Gallery ${idx}`}
                className="gallery-item"
                onClick={() => setSelectedGalleryImage(imgUrl)}
              />
            ))}
          </div>
        ) : (
          <p>No images available.</p>
        )}
      </div>
    );
  };

  // ---------------------------
  // Gallery Modal (Expanded View)
  // ---------------------------
  const GalleryModal = () => {
    return (
      <div className="gallery-modal" onClick={() => setSelectedGalleryImage(null)}>
        <img
          src={selectedGalleryImage}
          alt="Expanded Gallery"
          className="expanded-gallery-image"
        />
      </div>
    );
  };

  // ---------------------------
  // Optional: Dynamic Cursor Follower
  // ---------------------------
  const CursorFollower = () => {
    const [cursorPos, setCursorPos] = useState({ x: -50, y: -50 });
    useEffect(() => {
      const handleMouseMove = (e) => {
        setCursorPos({ x: e.clientX, y: e.clientY });
      };
      window.addEventListener("mousemove", handleMouseMove);
      return () => window.removeEventListener("mousemove", handleMouseMove);
    }, []);
    return (
      <div
        className="cursor-follower"
        style={{ transform: `translate(${cursorPos.x}px, ${cursorPos.y}px)` }}
      ></div>
    );
  };

  // ---------------------------
  // Main Render
  // ---------------------------
  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        {currentView === "create" ? <CreationView /> : <GalleryView />}
      </div>
      {selectedGalleryImage && <GalleryModal />}
      <ChatBot />
      <CursorFollower />
    </div>
  );
}

export default App;
